mapper.select:
SELECT
    count(1) AS count
FROM TB_RESERVATION TR
WHERE
    TR.USE_DAY = #{useDay}
  AND(
    TR.USE_START_TIME BETWEEN #{useStartTime} AND #{useEndTime}
   OR
    TR.USE_END_TIME BETWEEN #{useStartTime} AND #{useEndTime}
   OR
    #{useStartTime} BETWEEN TR.USE_START_TIME AND TR.USE_END_TIME
    )
